    ON SCHEDULE
        EVERY 1 DAY
    DO
    BEGIN
        DELETE FROM publication WHERE UNIX_TIMESTAMP(`created`) < (UNIX_TIMESTAMP()-720*3600);
        DELETE FROM photo_gallery WHERE UNIX_TIMESTAMP(`created`) < (UNIX_TIMESTAMP()-2160*3600);
    END $$

